fs = require("fs");
http = require("http");

fs.mkdir("public/pages", (err) => {
    if(err) console.log(err)
    else console.log("Folder created")
})

fs.mkdir("public/css", (err) => {
    if(err) console.log(err)
    else console.log("Folder css created")
})

fs.writeFile("public/pages/index.html", "Index", (err) => {
    if(err) console.log(err)
    else console.log("index File css created")
})
fs.writeFile("public/pages/home.html", "Home", (err) => {
    if(err) console.log(err)
    else console.log("home File css created")
})
fs.writeFile("public/css/style.css", "Css", (err) => {
    if(err) console.log(err)
    else console.log("Css File css created")
})

const server = http.createServer((req,res) => {
    const url = req.url;
    if(url === "/index"){
        res.writeHead(200, {"Content-Type": "text/html"})
        res.write('Hello, this is my second server page');
        res.end();
    }
})

server.listen(9000, () => {
    console.log('Server is up and running');
});